function animateHomeSection() {
	var bodyWidth = -1 * parseInt(($('body').width() + 200));
	$(".bg-home").attr("style", "margin-left:"+bodyWidth+"px");
	$(".bg-home").show();
	$(".logo").animate({marginTop: "0px"}, 700);
	$(".opener").animate({marginTop: "0px"}, 700);
	//$(".bg-home").show(1000);
	  /*$( ".bg-home" ).animate({
			display: "block"
	  	}, 1000, function() {
		$(".right-panel").fadeIn(500);
	  });*/
	
	$(".bg-home").animate({marginLeft: "0px"}, 1000, "linear", function() {
		$(".arrow-top").animate({"opacity":1, "top":"90px"}, 1200);
		$(".arrow-bottom").animate({"opacity":1, "top":"752px"}, 1200);
		$(".video").animate({"opacity":1}, 1500, "linear", function() {
			$(".bg-green").fadeIn(1200);
		});
		$(".left-inner").fadeIn(1200);
	});
	
	$(".left-circle").animate({"opacity":1, "top":"55px", left:"0"}, 1200);
	$(".col").animate({"opacity":1, "marginRight":0}, 1200);
	$(".two-columns .column").animate({"opacity":1, "margin-top":0}, 1200);
	$(".customer-list").animate({"opacity":1, "top":0}, 1200);
	$(".gallery-holder a.btn-prev, .gallery-holder a.btn-next").animate({"opacity":1}, 1200);
	$(".contactus .contact-area").animate({"opacity":1, "marginTop":0}, 1200);
	$(".arrow-left").animate({"opacity":1, "left":"40px"}, 1200);
	$(".img-mobile").animate({"opacity":1, "left":"160px"}, 1200);
}

// init smooth scrolling
function initSmoothScroll() {
	var isWindowsPhone = /Windows Phone/.test(navigator.userAgent);
	var scrollDuration = 800;
	var scrollElement = jQuery('html, body');
	var win = jQuery(window);
	var activeClass = 'active';
	var correctionBlock = jQuery('#header');
	var isAnimating = false;

	jQuery('#nav ul li a').each(function() {
		var link = jQuery(this);
		var href = link.attr('href');
		if(href.indexOf('#') != 0) return;
		var target = jQuery(href);

		link.on('click', function(e) {
			var clickTarget = jQuery(link.attr('href'));
			if(clickTarget.length > 0) {
				e.preventDefault();
				var topOffset = clickTarget.offset().top - correctionBlock.height();
				
				isAnimating = true;
				link.closest('li').addClass(activeClass).siblings('li').removeClass(activeClass);
				if (isWindowsPhone) {
					window.scrollTo(window.scrollLeft, topOffset);
				} else {
					scrollElement.stop().animate({
						scrollTop: topOffset + 1
					},{
						duration: scrollDuration,
						complete: function(){
							isAnimating = false;
						}
					});
				}
			}
		});

		//function scrollHandler() {
//			if(win.scrollTop() >= target.offset().top - correctionBlock.height() && win.scrollTop() <= target.offset().top + target.innerHeight() - correctionBlock.height()) {
//				var corrLink = jQuery('a[href="#' + target.attr('id') + '"]');
//				if(!isAnimating) corrLink.closest('li').addClass(activeClass).siblings('li').removeClass(activeClass);
//			}
//		}
		//scrollHandler();
//		win.on('resize orientationchange scroll', scrollHandler)
	});
}
// smooth anchor links
function initAnchorLinks() {
	var animSpeed = 650,
		win  = jQuery(window);
		page = jQuery('html,body'),
		isWP8 = /MSIE 10.*Touch/.test(navigator.userAgent);
	var header = jQuery('#header');
	var links = jQuery('#nav ul li a');
	var heightHeader = header.find('#header').outerHeight();
	var openCloseApi = jQuery('.open-close').data('OpenClose');
	var mobileFix = 30;

	jQuery('.go').click(function(e) {
		e.preventDefault();
		var targetId = this.getAttribute('href');
		var calcOffset = targetId.length > 1 ? jQuery(targetId).offset().top : 0;
		if (win.width()<768) {
			calcOffset = targetId.length > 1 ? jQuery(targetId).offset().top - header.find('.header-holder').outerHeight() - mobileFix : 0;
		} 

		if (openCloseApi && header.outerWidth()<768) openCloseApi.hideSlide();

		if (isWP8) {
			win.scrollTop(calcOffset - heightHeader + mobileFix);
		} else {
			page.animate({
				scrollTop: calcOffset - heightHeader + mobileFix
			}, animSpeed);
		}
	});

	links.each(function(i) {
		var self = jQuery(this);
		var holder = jQuery(self.attr('href'));
		var heightHolder = holder.outerHeight();
		win.on('scroll', function(e) {
			var offsetHolder = holder.offset().top;
			e.preventDefault();
			var scroll = jQuery(this).scrollTop();
			var scrollTopCalc = offsetHolder - 50;
			if (scroll > scrollTopCalc && scroll  < offsetHolder - heightHeader  + heightHolder) {
				// links.parent().removeClass('active');
				if (!self.hasClass('active')) {
					self.parent().addClass('active');
				}
			} else {
				self.parent().removeClass('active');
			}
			if (scroll  + jQuery(this).outerHeight() > jQuery('body').outerHeight() - 30  ) {
				links.parent().removeClass('active');
				if (i == links.length-1) {
					self.parent().addClass('active')
				};
			};
		});
	});
};

// smooth anchor links
function initAnchors() {
	// common case:
	new SmoothScroll({
		extraOffset: $('.anchor-nav').height() || 0,
		anchorLinks: '.anchor-nav a',
		activeClasses: 'link',
		wheelBehavior: 'ignore',
		animDuration: 800
	});
}

